Row.code:
//Pfad unter dem das Foto abgespeichert werden soll, Dateiname jeweils Timestamp
var path = "/RaspberryFolder/" + JSONInput.stamp + ".jpg";

//Das enkodierte Foto wird unter dem Pfad binaer gespeichert
var params = {};
params['path'] = path;
params['content'] = JSONInput.photo;
me.SaveBinary(params);

//generiere ImageLink
var imageParams = {}
imageParams['path'] = "/RaspberryFolder/";
imageParams['nameMask'] = JSONInput.stamp + ".jpg";

//Link um Detailansicht-Mashup mit entsprechendem Foto
var mashupLink = "http://52.205.147.45/Thingworx/Runtime/index.html#mashup=DetailsMashup&link=" + me.GetFileListingWithLinks(imageParams).downloadLink;

//Timestamp wird als String Ã¼bergeben, Ã¼bertragenes Format
//muss zum korrekten Parsen angegeben werden. Zeitzone MESZ wird eingestellt
var dateValue = parseDate(JSONInput.stamp, "yyyy-MM-dd HH:mm:ss");
dateValue.setHours(dateValue.getUTCHours() - 2)

//Datenreihe als Infotable wird erstellt
var infotable =
Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
infoTableName : "InfoTable",
dataShapeName : "RaspberryImagePathShape"
});

infotable.AddRow({
photoView: mashupLink, 
info: JSONInput.info, 
mac: me.MACAddress, 
stamp: dateValue
});

//Methode um Datensatz im persistenten DataBaseThing abzuspeichern
var dataParams = {};
dataParams['values'] = infotable;
Things["DataBaseThing"].AddDataTableEntry(dataParams);

var infotable =
Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
infoTableName : "InfoTable",
dataShapeName : "RaspberryImagePathShape"
});

infotable.AddRow({
mac: me.MACAddress, 
});

var params = {}
params['values'] = infotable;

var result = Things["DataBaseThing"].FindDataTableEntries(params)
